sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

          // Add your code here
var rightNow = new GlideDateTime();

	var OverDueTask = new GlideRecord('x_58872_needit_needit');

	OverDueTask.addQuery('u_when_needed','<=',rightNow);
	OverDueTask.addQuery('u_when_needed','>',gs.hoursAgo(48));
	OverDueTask.addQuery('u_requested_for',current.u_requested_for);
	OverDueTask.query();
	var count = OverDueTask.getRowCount();
	gs.info("48 hours ago is "+gs.hoursAgo(48)+"D : " +current.u_requested_for + " count: "+ OverDueTask.getRowCount()+ " and count variable is "+ count);
	template.print("In addition, you have "+ (count-1)+ " more records which needs your imediate attention.");



})(current, template, email, email_action, event);
